    let [vars, setVars] = useState(null);
    const GetWinnerGame = async () => {
        try {
            const winner = 2;
            getWinner();
            let content = <div>winner</div>;

            useEffect(() => {
                setVars(content);
            })
        }
        catch (error) {

            console.error("Winner can't be retrieved...",error);

        }
    };


    return(
    <div>
        <div>
            <button onClick={getWinnerGame}>Find out the winner!</button>
            <br></br>   
