import React from "react";
import { getWinner } from "../utils/contractServices";
const content = <>ss</>;
function getWinnerButton({winner}) {

    const getWinnerGame = async () => {
        try {
            const winner = 2;
            await getWinner();
            content = (<div>{winner}s</div>);
        }
        catch (error) {
            content = <div></div>
            console.error("Winner can't be retrieved...",error);

        }
    };


    return(
    <div>
        <div>
            <button onClick={getWinnerGame}>Find out the winner!</button>
            <br></br>   
            <h1>Winner: {content}</h1>
        </div>
    </div>
    );
}


export default getWinnerButton;
