import React, { useEffect, useState} from "react";
import { getWinner } from "../utils/contractServices";
function getWinnerButton() {
    let content;
    let winner;
    const getWinnerGame = async () => {
        try {
             winner = await getWinner();
             console.log(winner);
            if (winner != null){
                content = <div>{winner}</div>;
            }
            else { content = <div></div>}

        }
        catch (error) {
            console.error("Winner can't be retrieved...",error);
        }
    };


    return(
    <div>
        <div>
            <button onClick={getWinnerGame}>Find out the winner!</button>
            <br></br>   
            <h1>Winner: {String(winner)}</h1>
        </div>
    </div>
    );
}


export default getWinnerButton;
